import React, { useState } from 'react';
import { Proof } from './types/proof';

const DataProviderPanel: React.FC = () => {
  const [dataRequestId, setDataRequestId] = useState<string>('');
  const [value, setValue] = useState<number | ''>('');
  const [proofs, setProofs] = useState<Proof[]>([]);

  const handleSubmit = async () => {
    if (value === '') {
      alert('Please enter a value.');
      return;
    }

    try {
      const response = await fetch('http://localhost:3001/data-response', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ value })
      });
      const { proof_id } = await response.json();

      const proofResponse = await fetch(`http://localhost:3001/data-response/${proof_id}`);
      const proofData = await proofResponse.json();
      setProofs((prev) => [...prev, proofData]);
      setValue('');
    } catch (error) {
      console.error(error);
    }
  };

  const handleDownload = (item: Proof) => {
    const blob = new Blob([JSON.stringify(item, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${item.id}.json`;
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '100px' }}>
      <h2>Data Provider</h2>
      <h4>data type: blood_pressure</h4>
      <div style={{ marginTop: '20px', alignContent: 'left' }}>
      <div style={{ margin: '10px' }}>
          <label>data_request_id: </label>
          <input
            type="text"
            value={dataRequestId}
            onChange={(e) => setDataRequestId(e.target.value)}
          />
        </div>
        <div style={{ margin: '10px' }}>
          <label>value: </label>
          <input
            type="number"
            value={value}
            onChange={(e) => setValue(Number(e.target.value))}
          />
        </div>
      {/* <input
          type="text"
          value={dataRequestId}
          onChange={(e) => setDataRequestId(e.target.value)}
          placeholder="Enter value"
        />
        <input
          type="number"
          value={value}
          onChange={(e) => setValue(Number(e.target.value))}
          placeholder="Enter value"
        /> */}
        <button
          onClick={handleSubmit}
          style={{
            marginLeft: '10px',
            padding: '8px 16px',
            borderRadius: '6px',
            fontSize: '14px',
            backgroundColor: '#388e3c',
            color: 'white',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Submit
        </button>
      </div>
      <ul style={{ marginTop: '40px', listStyle: 'none', padding: 0 }}>
        {proofs.map((item) => (
          <li key={item.id} style={{ textAlign: 'left', margin: '10px auto', border: '1px solid #ccc', padding: '10px', width: '60%', borderRadius: '6px' }}>
            <p><strong>ID:</strong> {item.id}</p>
            <p><strong>Data Request ID:</strong> {item.data_request_id}</p>
            <p><strong>Created At:</strong> {new Date(item.created_at).toLocaleString()}</p>
            <button
              onClick={() => handleDownload(item)}
              style={{
                marginTop: '10px',
                padding: '6px 12px',
                borderRadius: '4px',
                backgroundColor: '#1976d2',
                color: 'white',
                border: 'none',
                cursor: 'pointer'
              }}
            >
              Download
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DataProviderPanel;
