import React, { createContext, useContext, useState, ReactNode } from 'react';

export type DataRequest = {
  id: string;
  user_id: string;
  data_type: string;
  min: number;
  max: number;
  leq: boolean;
  geq: boolean;
  exp_date: number;
};

type DataRequestContextType = {
  dataRequests: DataRequest[];
  addDataRequest: (item: DataRequest) => void;
};

const DataRequestContext = createContext<DataRequestContextType | undefined>(undefined);

export const DataRequestProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [dataRequests, setDataRequests] = useState<DataRequest[]>([]);

  const addDataRequest = (item: DataRequest) => {
    setDataRequests((prev) => [...prev, item]);
  };

  return (
    <DataRequestContext.Provider value={{ dataRequests, addDataRequest }}>
      {children}
    </DataRequestContext.Provider>
  );
};

export const useDataRequestContext = (): DataRequestContextType => {
  const context = useContext(DataRequestContext);
  if (!context) {
    throw new Error('useDataRequestContext must be used within a DataRequestProvider');
  }
  return context;
};
